mapper.select:
select

from maoding_web_company_bill
where company_id = #{companyId}
and pay_type = #{payType}
and fee_type = #{feeType}
and payment_date = #{paymentDate}

  select a.*
  from maoding_web_company_bill a
  INNER JOIN maoding_web_company_bill_relation b on a.id = b.id
  where deleted = 0
  
    and a.company_id = #{companyId}
  
  
    and b.from_company_id = #{fromCompanyId}
  
  
    and b.from_company_id is null
  
  
    and b.to_company_id = #{toCompanyId}
  
  
    and b.to_company_id is null
  
  
    and a.pay_type = #{payType}
  
  
    and a.fee_type = #{feeType}
  
and a.payment_date = #{paymentDate}

select a.*
from maoding_web_company_bill a
INNER JOIN maoding_web_company_bill_relation b on a.id = b.id
where a.deleted = 0

  and a.company_id = #{companyId}


  and pay_type = #{payType}


  and fee_type = #{feeType}


  and target_id = #{targetId}
