mapper.select:
select deliver_task.id ,deliver_task.task_title as name ,deliver_task.task_content as description ,deliver_task.deadline as end_time ,deliver_task.create_by ,deliver_task.create_date ,if(deliver_task.complete_date is null,'0','1') as is_finished ,deliver_task.target_id ,company_user.id as response_id ,user_response.user_name as response_name ,if(response_task.complete_date is null,'0','1') as response_is_finished ,user_create.user_name as create_by_name from md_list_deliver deliver_task inner join maoding_web_my_task response_task on (response_task.target_id = deliver_task.id and response_task.task_type = 26) inner join maoding_web_company_user company_user on (company_user.id = response_task.handler_id) inner join maoding_web_account user_response on (user_response.id = company_user.user_id) inner join maoding_web_account user_create on (user_create.id = deliver_task.create_by)
inner join maoding_web_project_sky_drive node on ( deliver_task.target_id = node.id
and node.id = #{nodeId}
)
inner join maoding_web_project_task issue_task on (issue_task.id = node.task_id and issue_task.id = #{taskId})
where deliver_task.param4 = '0'
