mapper.select:
select

,

from maoding_web_org_dynamic
where field2='0'

  and  company_id = #{companyId,jdbcType=VARCHAR}


  and  dynamic_type = #{dynamicType,jdbcType=VARCHAR}

ORDER BY  create_date DESC

  limit #{startPage},#{endPage}

select SQL_CALC_FOUND_ROWS a.id,a.dynamic_title,a.company_id,a.dynamic_type,a.target_id,a.create_by,a.create_date,a.update_by,a.update_date,a.field1,a.field2,a.dynamic_content from maoding_web_org_dynamic a left join maoding_web_notice b on (a.target_id=b.id) left join maoding_web_notice_org c on (b.id=c.notice_id) WHERE a.field2='0'
and (((a.company_id = #{companyId,jdbcType=VARCHAR}) and (c.org_id is null or c.org_id = #{companyId,jdbcType=VARCHAR})) or ((a.company_id != #{companyId,jdbcType=VARCHAR}) and (c.org_id = #{companyId,jdbcType=VARCHAR})))
and a.dynamic_type = #{dynamicType,jdbcType=VARCHAR}
ORDER BY a.create_date DESC
limit #{startPage},#{endPage}

select
COUNT(1)
from maoding_web_org_dynamic
where field2='0'

  and  company_id = #{companyId,jdbcType=VARCHAR}


  and  dynamic_type = #{dynamicType,jdbcType=VARCHAR}

mapper.insert:
insert into maoding_web_org_dynamic (id, dynamic_title, company_id, 
  dynamic_type, target_id, create_by, 
  create_date, update_by, update_date, 
  field1, field2, dynamic_content
  )
values (#{id,jdbcType=VARCHAR}, #{dynamicTitle,jdbcType=VARCHAR}, #{companyId,jdbcType=VARCHAR}, 
  #{dynamicType,jdbcType=VARCHAR}, #{targetId,jdbcType=VARCHAR}, #{createBy,jdbcType=VARCHAR}, 
  #{createDate,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=VARCHAR}, #{updateDate,jdbcType=TIMESTAMP}, 
  #{field1,jdbcType=VARCHAR}, '0', #{dynamicContent,jdbcType=LONGVARCHAR}
  )
