mapper.select:
select field_list.* from maoding_dynamic_form_field field_list where deleted = 0
and field_list.form_id = #{formId}
and field_list.field_pid = #{fieldPid}
order by field_list.seq_y,seq_x

select field_list.* from maoding_dynamic_form_field field_list where field_list.deleted = 0 and field_list.field_pid is null
and field_list.form_id = #{formId}
order by field_list.seq_y,seq_x
