mapper.update:
update maoding_dynamic_form_group
set deleted = 1
where id = #{id,jdbcType=VARCHAR}

mapper.select:
SELECT

FROM
maoding_dynamic_form_group
WHERE
company_id = #{companyId}
AND
is_edit = #{isEdit}
AND
group_name = #{groupName}

select
process_type.id as process_id, process_type.status as process_status, process_type.type as process_type, process_type.form_type as form_type, form_list.id as form_id, form_list.form_name, form_list.documentation, if(form_list.id is null,null,if(form_list.company_id is null,1,0)) as form_is_system,
group_list.* from maoding_dynamic_form_group group_list
left join maoding_process_type process_type on ( process_type.deleted = 0 and process_type.form_type = group_list.id
and process_type.status = #{status}
) left join maoding_dynamic_form form_list on ( form_list.deleted = 0 and form_list.id = process_type.form_id )
where group_list.deleted = 0
and group_list.company_id = #{currentCompanyId}
order by group_list.seq
,process_type.seq
