mapper.select:
SELECT a.exp_flag,a.version_num,a.exp_no,a.type,a.id,a.company_id,a.company_user_id,e.user_name as user_name,a.exp_date,a.allocation_date, b.audit_person,c.user_name as audit_person_name,a.approve_status,b.approve_date , (select s.exp_use from maoding_web_exp_detail s where s.main_id=a.id ORDER BY s.seq LIMIT 1) AS exp_use,d.exp_type as expType, (select m.is_new from maoding_web_exp_audit m where m.main_id=a.id and m.audit_person=b.audit_person ORDER BY m.create_date DESC LIMIT 1) AS is_new, (select sum(m.exp_amount) from maoding_web_exp_detail m where m.main_id=a.id) as exp_sum_amount,(select COUNT(1) from maoding_web_exp_detail m where m.main_id=a.id) as detail_count from maoding_web_exp_main a INNER JOIN maoding_web_exp_detail d on a.id = d.main_id LEFT JOIN maoding_web_exp_audit b on a.id = b.main_id and b.is_new='Y' LEFT JOIN maoding_web_company_user c on b.audit_person = c.id and c.company_id = a.company_id LEFT JOIN maoding_web_company_user e on a.company_user_id = e.id and e.company_id = a.company_id WHERE 1=1 AND a.approve_status != '4'
GROUP BY a.id order by a.create_date DESC
LIMIT #{startPage},#{endPage}

