mapper.select:
 select SQL_CALC_FOUND_ROWS
 
 from maoding_web_my_task
 where 1=1 and param4='0' and status!='2'
 
  AND handler_id = #{handlerId,jdbcType=VARCHAR}
 
 
  AND company_id = #{companyId,jdbcType=VARCHAR}
 
 
   AND status = #{status,jdbcType=VARCHAR}

 
  AND target_id = #{targetId,jdbcType=VARCHAR}
 
 
   AND project_id = #{projectId,jdbcType=VARCHAR}
 
 
   AND task_type = #{taskType,jdbcType=INTEGER}
 
 
   AND target_id = #{targetId}
 
 
   and task_type in
   
     #{type}
   
 
 order by status,create_date desc
 
   LIMIT #{startPage},#{endPage}

select
count(1)
from maoding_web_my_task
where 1=1 and param4='0'

  AND handler_id = #{handlerId,jdbcType=VARCHAR}


  AND company_id = #{companyId,jdbcType=VARCHAR}


   AND status = #{status,jdbcType=VARCHAR}


  AND target_id = #{targetId,jdbcType=VARCHAR}


  AND project_id = #{projectId,jdbcType=VARCHAR}


  AND task_type = #{taskType,jdbcType=INTEGER}

mapper.update:
 update maoding_web_my_task
 
   
     status = #{status,jdbcType=VARCHAR},
   
   
     param2 = #{param2,jdbcType=VARCHAR},
   
   
     param4 = #{param4,jdbcType=VARCHAR},
   
   
     complete_date = now(),
   
   update_date = now(),
 
 where 1=1
   
     and status='0'
   
  
     and target_id = #{targetId,jdbcType=VARCHAR}
  
  
    and  task_type=#{taskType,jdbcType=INTEGER}
  
 
   and  status!=#{taskStatus,jdbcType=VARCHAR}
 
 
    and  company_id=#{companyId,jdbcType=VARCHAR}
 
 
    and  project_id=#{projectId,jdbcType=VARCHAR}
 
 
   and handler_id = #{handlerId,jdbcType=VARCHAR}

 
   and task_type in
   
     #{type}

  update maoding_web_my_task
  SET
  
    status = #{status,jdbcType=VARCHAR},

  
    param2 = #{param2,jdbcType=VARCHAR},
  
  
    param4 = #{param4,jdbcType=VARCHAR},
  
  
    complete_date = now(),
  
  update_date = now()
  where status ='0'
  
    and target_id = #{targetId,jdbcType=VARCHAR}
  
  
    and  task_type=#{taskType,jdbcType=INTEGER}
  
  
    and  company_id=#{companyId,jdbcType=VARCHAR}
  
  
    and  project_id=#{projectId,jdbcType=VARCHAR}
  
  
    and handler_id = #{handlerId,jdbcType=VARCHAR}

update maoding_web_my_task
set param4 = '1',
update_date = now()
where 1=1

  and target_id = #{targetId,jdbcType=VARCHAR}


  and  task_type=#{taskType,jdbcType=INTEGER}


  and  company_id=#{companyId,jdbcType=VARCHAR}


  and  project_id=#{projectId,jdbcType=VARCHAR}


  and handler_id = #{handlerId,jdbcType=VARCHAR}

mapper.select:
select deliver_task.id -- 交付任务编号 ,deliver_task.task_title as name -- 交付任务名称 ,deliver_task.task_content as description -- 说明 ,deliver_task.deadline as end_time -- 截止时间 ,deliver_task.create_by -- 发起人 ,deliver_task.create_date -- 发起时间 ,if(deliver_task.complete_date is null,'0','1') as is_finished -- 是否已经结束 ,account.id as response_id -- 负责人编号 ,account.user_name as response_name -- 负责人姓名 ,if(response_task.complete_date is null,'0','1') as response_is_finised -- 负责人是否已结束任务 from md_list_deliver deliver_task -- 交付任务 inner join maoding_web_my_task response_task on (response_task.target_id = deliver_task.id and response_task.task_type = 26) -- 负责人任务 inner join maoding_web_company_user company_user on (company_user.id = response_task.handler_id) -- 负责人角色 inner join maoding_web_account account on (account.id = company_user.user_id) -- 用户名
inner join maoding_web_project_sky_drive node on ( deliver_task.target_id = node.id
and node.id = #{nodeId}
) -- 交付目录
inner join maoding_web_project_task issue_task on (issue_task.id = node.task_id and issue_task.id = #{taskId}) -- 签发任务
