mapper.insert:
insert into maoding_web_company_property (id, company_id, field_name,
                                          unit_name, be_selected, be_default,sequencing,
                                          create_date, create_by)
values

  (
      replace(uuid(),'-','')
      ,#{companyId}#{entity.companyId}
      ,#{entity.fieldName},#{entity.unitName}
      ,#{beSelected,jdbcType=TINYINT}#{entity.beSelected,jdbcType=TINYINT}
      ,#{beDefault,jdbcType=TINYINT}#{entity.beDefault,jdbcType=TINYINT}
      ,(#{entity.sequencing}+#{idx})#{idx}
      ,#{createDate}now()
      ,#{createBy}#{entity.createBy}
  )

mapper.update:
update maoding_web_company_property
set deleted=1
where id = #{id}

