SELECT a.id, a.company_id, a.user_id, a.user_name, a.relation_type, a.audit_status, a.employee_type, a.employee_status, a.entry_time, a.departure_time, a.illustration, a.phone, a.email, a.departure_reason, b.cellphone, c.sex, d.seq, d.server_station as org_server_station, e.id as admin_flag, f.role_names, f.role_ids FROM maoding_web_company_user a LEFT JOIN maoding_web_account b ON b.id=a.user_id LEFT JOIN maoding_web_user c ON a.user_id=c.id LEFT JOIN maoding_web_org_user d on a.id = d.cu_id and a.company_id = d.company_id and a.user_id = d.user_id LEFT JOIN maoding_web_team_operater e on a.company_id=e.company_id and a.user_id = e.user_id LEFT JOIN (SELECT ru.user_id,ru.company_id,ru.org_id,GROUP_CONCAT(r.name) as role_names,GROUP_CONCAT(r.id) as role_ids FROM maoding_web_role_user ru,maoding_web_role r where ru.role_id = r.id GROUP by ru.org_id,ru.user_id) f on a.company_id = f.org_id and a.user_id = f.user_id WHERE (d.org_id=#{orgId,jdbcType=VARCHAR} or (d.org_id is null and a.company_id=#{orgId,jdbcType=VARCHAR}))
AND a.audit_status= #{auditStatus,jdbcType=VARCHAR}
AND (a.user_name LIKE '%#{keyword,jdbcType=VARCHAR}%' OR b.cellphone LIKE '%#{keyword,jdbcType=VARCHAR}%')
ORDER BY d.seq
LIMIT #{startPage},#{endPage}

SELECT COUNT(1) from ( SELECT DISTINCT a.id FROM maoding_web_company_user a LEFT JOIN maoding_web_account b ON b.id=a.user_id LEFT JOIN maoding_web_org_user c on a.id = c.cu_id and a.company_id = c.company_id and a.user_id = c.user_id WHERE (c.org_id=#{orgId,jdbcType=VARCHAR} or (c.org_id is null and a.company_id=#{orgId,jdbcType=VARCHAR}))
AND a.audit_status= #{auditStatus,jdbcType=VARCHAR}
AND (a.user_name LIKE '%#{keyword,jdbcType=VARCHAR}%' OR b.cellphone LIKE '%#{keyword,jdbcType=VARCHAR}%')
)a22

SELECT a.id, a.company_id, a.user_id, a.user_name, a.relation_type, a.audit_status, a.employee_type, a.employee_status, a.entry_time, a.departure_time, a.illustration, a.phone, a.email, a.departure_reason, b.cellphone, c.sex, d.seq, d.server_station as org_server_station, e.occupation_registration, f.positional_title FROM maoding_web_company_user a LEFT JOIN maoding_web_account b ON b.id=a.user_id LEFT JOIN maoding_web_user c ON a.user_id=c.id LEFT JOIN maoding_web_org_user d on a.id = d.cu_id and a.company_id = d.company_id and a.user_id = d.user_id LEFT JOIN ( SELECT uq.user_id,dd.name as occupation_registration FROM maoding_web_user_qualifications uq,maoding_data_dictionary dd where dd.id=uq.reg_certificate_level)e ON e.user_id=a.user_id LEFT JOIN ( SELECT utn.user_id,dd.name as positional_title FROM maoding_web_user_technical utn,maoding_data_dictionary dd where dd.id=utn.technical_level)f ON f.user_id=a.user_id WHERE (d.org_id=#{orgId,jdbcType=VARCHAR} or (d.org_id is null and a.company_id=#{orgId,jdbcType=VARCHAR}))
AND a.audit_status= #{auditStatus,jdbcType=VARCHAR}
AND (a.user_name LIKE '%#{keyword,jdbcType=VARCHAR}%' OR b.cellphone LIKE '%#{keyword,jdbcType=VARCHAR}%')
ORDER BY d.seq
LIMIT #{startPage},#{endPage}

select DISTINCT a.user_id as userId, a.user_name as userName from maoding_web_company_user a
left join maoding_web_org_user b on a.id = b.cu_id
where 1 = 1

	AND b.org_id = #{orgId,jdbcType=VARCHAR}


	AND a.company_id = #{companyId,jdbcType=VARCHAR}

SELECT a.*, b.cellphone, c.sex, d.occupation_registration, e.positional_title FROM maoding_web_company_user a LEFT JOIN maoding_web_account b ON b.id=a.user_id LEFT JOIN maoding_web_user c ON a.user_id=c.id LEFT JOIN ( SELECT uq.user_id,dd.name as occupation_registration FROM maoding_web_user_qualifications uq,maoding_data_dictionary dd where dd.id=uq.reg_certificate_level)d ON d.user_id=a.user_id LEFT JOIN ( SELECT utn.user_id,dd.name as positional_title FROM maoding_web_user_technical utn,maoding_data_dictionary dd where dd.id=utn.technical_level)e ON e.user_id=a.user_id LEFT JOIN (SELECT a1.user_id ,a1.server_station , b1.id as depart_id,b1.company_id,b1.depart_name,b1.depart_seq,b1.depart_path,b1.depart_level FROM maoding_web_org_user a1 LEFT JOIN maoding_web_depart b1 on a1.org_id=b1.id GROUP BY a1.org_id,a1.user_id ) g on g.company_id=a.company_id and g.user_id =a.user_id WHERE (CASE (SELECT @ct:=COUNT(*) FROM maoding_web_company c WHERE c.id=#{orgId,jdbcType=VARCHAR}) WHEN 0 THEN g.depart_path LIKE CONCAT('%',#{orgId,jdbcType=VARCHAR},'%') ELSE a.company_id=#{orgId,jdbcType=VARCHAR} END)
AND a.audit_status= #{auditStatus,jdbcType=VARCHAR}
AND (a.user_name LIKE '%#{keyword,jdbcType=VARCHAR}%' OR b.cellphone LIKE '%#{keyword,jdbcType=VARCHAR}%')
ORDER BY a.seq,a.update_date desc
LIMIT #{startPage},#{endPage}

SELECT COUNT(1) from ( SELECT DISTINCT a.id FROM maoding_web_company_user a LEFT JOIN (SELECT a1.user_id ,a1.server_station , b1.id as depart_id,b1.company_id,b1.depart_name,b1.depart_seq,b1.depart_path FROM maoding_web_org_user a1 LEFT JOIN maoding_web_depart b1 on a1.org_id=b1.id WHERE a1.org_id!=a1.company_id GROUP BY a1.org_id,a1.user_id ORDER BY a1.user_id) g on g.company_id=a.company_id and g.user_id =a.user_id WHERE (CASE (SELECT @ct:=COUNT(*) FROM maoding_web_company c WHERE c.id=#{orgId,jdbcType=VARCHAR}) WHEN 0 THEN g.depart_path LIKE CONCAT('%',#{orgId,jdbcType=VARCHAR},'%') ELSE a.company_id=#{orgId,jdbcType=VARCHAR} END)
AND a.audit_status= #{auditStatus,jdbcType=VARCHAR}
AND (a.user_name LIKE '%#{keyword,jdbcType=VARCHAR}%' OR b.cellphone LIKE '%#{keyword,jdbcType=VARCHAR}%')
) a22
