mapper.select:
select

from maoding_process_type
where company_id = #{companyId}
and target_type =  #{targetType}
and status = 1

mapper.insert:
insert into maoding_process_type (id, company_id, target_type, 
  status,type, deleted, create_date,
  create_by, update_date, update_by
  )
values (#{id,jdbcType=VARCHAR}, #{companyId,jdbcType=VARCHAR}, #{targetType,jdbcType=VARCHAR}, 
  #{status,jdbcType=INTEGER}, #{type,jdbcType=INTEGER}, #{deleted,jdbcType=INTEGER}, #{createDate,jdbcType=TIMESTAMP},
  #{createBy,jdbcType=VARCHAR}, #{updateDate,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=VARCHAR}
  )

mapper.select:
select process_ordered.* from ( select group_type.type_name as group_name, process_type.* from md_type_process_custom process_type inner join md_type_process_group_custom group_type on (group_type.type_id = process_type.group_type_id)
where process_type.company_id is null or process_type.company_id = #{currentCompanyId}
order by process_type.company_id desc ) process_ordered group by process_ordered.type_id,process_ordered.group_type_id
