mapper.select:
select group_type.id as form_group_id, group_type.group_name as group_name, process_type.id as process_type_id, dynamic_form.* from maoding_dynamic_form_group group_type left join maoding_process_type process_type on group_type.id = process_type.form_type and process_type.deleted = 0 left join maoding_dynamic_form dynamic_form on (process_type.form_id = dynamic_form.id) where 1=1
and group_type.company_id = #{currentCompanyId}
order by group_type.seq,process_type.seq

select
from maoding_process_type where form_id = #{id}
and (company_id = #{currentCompanyId})
