mapper.select:
select

from maoding_process_type
where company_id = #{companyId}
and target_type =  #{targetType}
and status = 1

mapper.insert:
insert into maoding_process_type (id, company_id, target_type, condition_field_id,
  status,type, deleted,form_id,notice_type,seq,
  create_date,create_by, update_date, update_by
  )
values (#{id,jdbcType=VARCHAR}, #{companyId,jdbcType=VARCHAR}, #{targetType,jdbcType=VARCHAR}, #{conditionFieldId,jdbcType=VARCHAR},
  #{status,jdbcType=INTEGER}, #{type,jdbcType=INTEGER}, #{deleted,jdbcType=INTEGER},
   #{formId,jdbcType=VARCHAR}, #{noticeType,jdbcType=INTEGER}, #{seq,jdbcType=INTEGER},
  #{createDate,jdbcType=TIMESTAMP},
  #{createBy,jdbcType=VARCHAR}, #{updateDate,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=VARCHAR}
  )

mapper.select:
