mapper.select:
select *
from maoding_web_project a
where
    a.pstatus='0'
    
        and a.id=#{id}
    
    
        and a.company_id=#{companyId}
    
    
        and a.project_no=#{projectNo}
    
    
        and a.project_name=#{projectName}
    
    
        and a.project_create_date=#{createDate}

SELECT id,name from maoding_data_dictionary where name in
#{name}
UNION SELECT id,title as name from md_list_const_custom where classic_id = '33' and title in
#{name}

mapper.sql:
and project_list.pstatus = '0'

    and project_list.id = #{id}


    and project_list.id in
        
            #{thisId}
        


    and ifnull(project_list.project_create_date,project_list.create_date) >= #{projectCreateDateStart,jdbcType=TIMESTAMP}


    and addDate(#{projectCreateDateEnd,jdbcType=TIMESTAMP},1) > ifnull(project_list.project_create_date,project_list.create_date)


    and project_list.contract_date >= #{signDateStart,jdbcType=TIMESTAMP}


    and addDate(#{signDateEnd,jdbcType=TIMESTAMP},1) > project_list.contract_date


    and project_list.company_bid = #{partB}


    and project_list.company_id = #{createCompany}


    and concat(project_list.province,project_list.city) = #{address}

mapper.select:
select project_list.* ,ifnull(project_list.project_create_date,project_list.create_date) as projectCreateDate ,project_list.contract_date as signDate ,concat(project_list.province,project_list.city) as address ,concat(create_company.company_name,'/',create_company_user.user_name) as createCompany ,part_b_company.company_name as partyB from maoding_web_project project_list inner join md_project_member member_list on ( member_list.project_id = project_list.id
and member_list.user_id = #{accountId}
and member_list.company_id = #{currentCompanyId}
) inner join maoding_web_company create_company on (create_company.status = '0' and create_company.id = project_list.company_id) left join maoding_web_company_user create_company_user on (create_company_user.company_id = project_list.company_id and create_company_user.user_id = project_list.create_by) left join maoding_web_company part_b_company on (part_b_company.status = '0' and part_b_company.id = project_list.company_bid)
group by project_list.id

select 
    if(default_built_type.id is null,null,1) as is_default,
    group_concat(distinct ifnull(custom_built_type.type_name,default_built_type.type_name) as built_type,
    project_list.*
from maoding_web_project project_list
    left join md_type_built_const default_built_type on (
        find_in_set(default_built_type.id,project_list.built_type)
        
            and default_built_type.type_name in
            
                #{aBuildName}
            
        
    )
    left join md_type_built_custom custom_built_type on (
        find_in_set(custom_built_type.id,project_list.built_type)
        
            and custom_built_type.type_name in
            
                #{aBuildName}
            
        
    )

    

group by project_list.id
