select a.id, a.project_id, a.pid, a.sky_drive_path, a.file_name, a.file_path, a.company_id, a.type, a.file_size, a.file_group, a.is_customize, a.file_ext_name, a.param4, a.task_id, a.status, a.create_date, a.update_date, a.update_by, a.task_id, (select cu.user_name from maoding_web_company_user cu where user_id = a.update_by and company_id = a.company_id) AS create_by,company.company_name from maoding_web_project_sky_drive a LEFT JOIN maoding_web_company company ON company.id=a.`company_id` where a.status='0'
and (type = 0 or type = 1 or type = 2)
and (type = 0 or type = 2 or type = 30 or type = 40 or type = 41 or type=50)
AND project_id = #{projectId,jdbcType=VARCHAR}
AND a.company_id= #{companyId,jdbcType=VARCHAR}
AND pid = #{pid,jdbcType=VARCHAR}
AND pid is NULL
and (
file_name=#{fileName}
)
order by param4, create_date

select a.id, a.project_id, a.pid, a.sky_drive_path, a.file_name, a.file_path, a.company_id, a.type, a.file_size, a.file_group, a.is_customize, a.file_ext_name, a.param4, a.task_id, a.status, a.create_date, a.update_date, a.update_by, a.task_id, (select cu.user_name from maoding_web_company_user cu where user_id = a.update_by and company_id = a.company_id) AS create_by,company.company_name from maoding_web_project_sky_drive a LEFT JOIN maoding_web_company company ON company.id=a.`company_id` where a.status='0'
and (type = 0 or type = 1 or type = 2)
and (type = 0 or type = 1 or type = 2 or type = 30 or type = 40 or type = 41 or type=50)
AND project_id = #{projectId,jdbcType=VARCHAR}
AND a.company_id= #{companyId,jdbcType=VARCHAR}
AND pid = #{pid,jdbcType=VARCHAR}
AND pid is NULL
and (
file_name=#{fileName}
)
order by param4, create_date

select id, project_id, pid, sky_drive_path, file_name, file_path, company_id, type, file_size, file_group, is_customize, file_ext_name, param4, task_id,status,create_date, update_date, (select cu.user_name from maoding_web_company_user cu where user_id = a.update_by and company_id = a.company_id) AS create_by from maoding_web_project_sky_drive a where a.status='0' and (type = 0 or type = 1)
AND project_id = #{projectId,jdbcType=VARCHAR}
AND pid = #{pid,jdbcType=VARCHAR}
and a.create_date <= #{syncDate,jdbcType=VARCHAR}
order by create_date desc

