SELECT t.id,t.project_id,t.pid,t.sky_drive_path,t.file_name,t.file_path,t.company_id,t.type,t.file_size, t.file_group,t.is_customize,t.file_ext_name,t.param4,t.task_id,t.status,t.target_id,t.create_date, t.update_date,t.update_by,us.`user_name` AS create_by FROM maoding_web_project_sky_drive t LEFT JOIN maoding_web_company_user us ON t.`create_by`=us.`user_id` AND t.`company_id`=us.`company_id` where status=0
and t.project_id=#{projectId}
